  inset: 0;
  background: rgba(20, 30, 38, 0.75);
  backdrop-filter: blur(4px);
}

.rts-payload-preview-content {
  position: relative;
  width: min(640px, calc(100vw - 48px));
  max-height: calc(100vh - 96px);
  background: linear-gradient(147deg, #162229 0%, #1E2E39 52%, #222B31 100%);
