  display: flex;
  gap: 12px;
}

.rts-payload-preview-copy,
.rts-payload-preview-cancel,
.rts-payload-preview-confirm {
  padding: 10px 20px;
  border: none;
  border-radius: 12px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
